INSERT INTO `dw_dataqualityReportLookup` (`indicator`, `indicatorType`, `nameen`, `namefr`, `definitionen`, `definitionfr`, `subGroupEn`, `subGroupFr`, `indicatorDenominator`) VALUES
(1, 1, 'Pediatric patients with missing day, month or year of birth', 'Les patients de pédiatrie avec le jour, mois ou année de naissance manquant', '<b> Numerator: </b>Registered pediatric patients with missing day, month or year of birth who were registered during the specified time period (patients, formid = 15 )<p/><b> Denominator: </b>All registered pediatric patients who were registered during the specified time period (patients, formid = 15)', '<b> Numerateur: </b>Enregistré les patients pédiatriques avec manquant de jour, mois ou année de naissance qui ont été enregistrées au cours de la période de temps spécifiée (patients, formid = 15) <p/><b>Denominateur: </b>Tous les patients pédiatriques ayant étaient enregistrés au cours de la période de temps spécifiée (patients, formid = 15)', 'Demographic', 'Démographique', -1),
(2, 1, 'Adult patients with missing month or year of birth', 'Les patients adultes avec le jour, mois ou année de naissance manquant', '<b>Numerator: </b>Registered adult patients with missing month or year of birth AND missing age in years who were registered during the specified time period (patients, formid = 10) <p/><b>Denominator: </b>All registered adult patients who were registered during the specified time period (patients, formid = 10)', '<b>Numerateur: </b>Immatriculée patients adultes disparus le mois ou l’année de naissance et manque d’âge en années qui ont été enregistrées au cours de la période de temps spécifiée (patients, formid = 10) <p/><b>Denominateur: </b>Tous les patients adultes qui ont été enregistrées au cours de la période de temps spécifiée (patients, formid = 10)', 'Demographic', 'Démographique', -2),
(3, 1, 'All patients with missing age detail', 'Tous les patients avec détails de l’âge manquant', '<b>Numerator: </b>Numerator for C.1 plus C.2 (patients, formid = 10, 15) <p/><b>Denominator: </b>Denominator for C.1 plus C.2 (patients, formid = 10, 15)', '<b>Numerateur: </b>Numérateur C.1 et C.2 (patients, formid = 10 à 15) <p/><b>Denominateur: </b>Dénominateur pour C.1 et C.2 (patients, formid = 10 à 15)', 'Demographic', 'Démographique', -3),
(4, 1, 'Pediatric patient clinical visit with height not recorded; OR height data recorded but not machine readable (contains symbols or words); OR numeric value recorded but with no unit of measure.', 'Visite clinique de patients pédiatriques avec données de hauteur non enregistrée; OU avec données de hauteur incorrectes (contient des symboles ou des mots); OU avec valeur numérique mais sans unité de mesure.', '<b>Numerator: </b>Clinical visit held but height not recorded at any clinical visit; OR height data recorded but not machine readable (contains symbols or words); OR numeric value recorded but with no unit of measure, for anyvisitdate during the specified time period (visits, formid = 16, 17, 29, 31) <p/><b>Denominator: </b>Any pediatric clinical visitdate during the specified time period (visits, formid = 16, 17, 29, 31)', '<b>Numerateur: </b>Visite clinique tenue mais hauteur pas enregistré lors de toute visite clinique ; OU hauteur données enregistrement mais pas déchiffrable (contient des symboles et mots) ; Valeur numérique OR enregistré mais avec aucune unité de mesure, d’anyvisitdate au cours de la période de temps spécifiée (visites, formid = 16, 17, 29, 31) <p/><b>Denominateur: </b>Tout visitdate clinique pédiatrique au cours de la période de temps spécifiée (visites, formid = 16, 17, 29, 31)', 'Clinical', 'Cliniques', -4),
(5, 1, 'Adult patient clinical visit with height not recorded at least once after age 18; OR height data recorded but not machine readable (contains symbols or words); OR numeric value recorded but with no unit of measure.', 'Visite clinique de patients adultes avec données de hauteur non enregistrées au moins une fois après 18 ans ; OU avec données de hauteur incorrectes(contient des symboles et mots) ; Ou avec valeur numérique mais sans unité de mesure.', '<b>Numerator: </b>Clinical visit held but height not recorded at least once after age 18 ; OR height data recorded but not machine readable (contains symbols or words); OR numeric value recorded but with no unit of measure, for any  visitdate during the specified time period (e.g. once a machine-readable height with unit of measure is recorded for a patient who is 18 or older, subsequent clinical visits with missing height data would not appear in the numerator)(visits, formid = 1, 2, 27, 28) <p/><b>Denominator: </b>Any adult clinical  visitdate during the specified time period, up until and including a clinical visit where a height is first recorded after age 18 (visits, formid = 1, 2, 27, 28)', '<b>Numerateur: </b>Visite clinique tenue mais hauteur pas enregistré au moins une fois après 18 ans ; OU hauteur données enregistrement mais pas déchiffrable (contient des symboles et mots) ; OU une valeur numérique <p/><b>Denominateur: </b>Any adult clinical  visitdate during the specified time period, up until and including a clinical visit where a height is first recorded after age 18 (visits, formid = 1, 2, 27, 28)', 'Clinical', 'Cliniques', -5),
(6, 1, 'All visits with missing height information for both ', 'Toutes les visites cliniques avec informations de hauteur manquant ou incorrectes', '<b>Numerator: </b>Numerator for C.4 plus C.5 (visits, formid = 1, 2, 16, 17, 27, 28, 29, 31) <p/><b>Denominator: </b>Denominator for C.4 plus C.5 (visits, formid = 1, 2, 16, 17, 27, 28, 29, 31)', '<b>Numerateur: </b> Numérateur pour C.4 plus C.5 (visites, formid = 1, 2, 16, 17, 27, 28, 29, 31)<p/><b>Denominateur: </b>Dénominateur pour C.4 plus C.5 (visites, formid = 1, 2, 16, 17, 27, 28, 29, 31)', 'Clinical', 'Cliniques', -6),
(7, 1, 'Pediatric patient clinical visit with weight not recorded; OR weight data recorded but not machine readable; OR weight data recorded but no unit of measure.', 'Visites cliniques de patients pédiatriques avec informations de poids non enregistrées; OU avec informations de poids incorrectes; OU avec données de poids enregistrées mais sans unité de mesure.', '<b>Numerator: </b>Pediatric clinical visit held but weight not recorded at any clinical visit; OR weight data recorded but not machine readable (contains symbols or words); OR numeric value recorded but with no unit of measure, for any visitdate during the specified time period (visits, formid = 16, 17, 29, 31) <p/><b>Denominator: </b>Any pediatric clinical visitdate during the specified time period (visits, formid = 16, 17, 29, 31)', '<b>Numerateur: </b>Visite clinique pédiatrique tenue mais poids pas constaté lors de toute visite clinique ; OU poids données enregistrement mais pas déchiffrable (contient des symboles et mots) ; Valeur numérique OR enregistré mais avec aucune unité de mesure, pour tout visitdate au cours de la période de temps spécifiée (visites, formid = 16, 17, 29, 31) <p/><b>Denominateur: </b>Tout visitdate clinique pédiatrique au cours de la période de temps spécifiée (visites, formid = 16, 17, 29, 31)', 'Clinical', 'Cliniques', -7),
(8, 1, 'Adult patient clinical visit with weight not recorded; OR weight data recorded but not machine readable; OR weight data recorded but no unit of measure.', 'Visites cliniques de patients adultes avec informations de poids non enregistrées; OU avec informations de poids incorrectes; OU avec données de poids enregistrées mais sans unité de mesure.', '<b>Numerator: </b>Adult clinical visit held but weight not recorded at any clinical visit; OR weight data recorded but not machine readable (contains symbols or words); OR numeric value recorded but with no unit of measure, for any visitdate during the specified time period (visits, formid = 1, 2, 27, 28) <p/><b>Denominator: </b>Any adult clinical visitdate during the specified time period (visits, formid = 1, 2, 27, 28)', '<b>Numerateur: </b>Visite clinique adulte détenus mais poids pas constaté lors de toute visite clinique ; OU poids données enregistrement mais pas déchiffrable (contient des symboles et mots) ; Valeur numérique OR enregistré mais avec aucune unité de mesure, pour tout visitdate au cours de la période de temps spécifiée (visites, formid = 1, 2, 27, 28)<p/><b>Denominateur: </b>N’importe quel adulte visitdate clinique au cours de la période de temps spécifiée (visites, formid = 1, 2, 27, 28)', 'Clinical', 'Cliniques', -5),
(9, 1, 'Any patient clinical visit with weight not recorded; OR weight data recorded but not machine readable; OR weight data recorded but no unit of measure.', 'Toutes les visites cliniques avec informations de poids non pas enregistrées; OU avec informations de poids incorrectes; OU avec données numériques mais sans unité de mesure.', '<b>Nominator: </b>Numerator for C.7 plus C.8 (visits, formid = 1, 2, 16, 17, 27, 28, 29, 31) <p/><b>Denominator: </b>Denominator for C.7 plus C.8 (visits, formid = 1, 2, 16, 17, 27, 28, 29, 31)', '<b>Numerateur: </b>Numérateur pour C.7 plus C.8 (visites, formid = 1, 2, 16, 17, 27, 28, 29, 31) <p/><b>Denominateur: </b>Denominator for C.7 plus C.8 (visits, formid = 1, 2, 16, 17, 27, 28, 29, 31)', 'Clinical', 'Cliniques', -33),
(10, 1, 'Any clinical visit with no data recorded for pregnancy status question  among women aged 15-49 or of unknown age', 'Toutes les visites cliniques sans données enregistrées pour les questions sur leur état de grossesse, concernant les femmes de 15-49 ans ou d’âge inconnu', '<b>Numerator: </b>Clinical visit held but no data recorded for pregnancy status question for women aged 15-49 or of unknown age (visits, formid = 1, 2, 27, 28) <p/><b>Denominator: </b>Any adult clinical visit for women aged 15-49 or of unknown age, with visitdate during the specified time period (visits, formid = 1, 2, 27, 28)', '<b>Numerateur: </b>Tenue de la visite clinique, mais aucune donnée enregistrée pour une question de statut de grossesse pour les femmes n’âgées de 15-49 ou d’âge inconnu (visites, formid = 1, 2, 27, 28) <p/><b>Denominateur: </b>N’importe quel adulte visite clinique pour femmes âgés de 15 à 49 ou d’âge inconnu, avec visitdate au cours de la période de temps spécifiée (visites, formid = 1, 2, 27, 28)', 'Clinical', 'Cliniques', -10),
(11, 1, 'Pediatric patient clinical visit with no ART eligibility or reason for eligibility recorded for patients prior to initiation of a life-long ART regimen', 'Visites cliniques pédiatriques sans informations d’éligibilité ARV ni raison d’éligibilité enregistrées pour les patients avant l’initiation d’un régime ARV permanent', '<b>Numerator: </b>Pediatric clinical visit held but no ART eligibility or reason for eligibility recorded for patients prior to initiation of a life-long ART regimen (visits, formid = 16, 17) <p/><b>Denominator: </b>Any pediatric clinical visit up until visitdate for prescription of multi-drug ART regimen where “for prophylaxis” is not marked, with visitdate during the specified time period ', '<b>Numerateur: </b>Visite clinique pédiatrique, tenue mais pas d’admissibilité ART ni raison d’éligibilité enregistrés pour les patients avant l’instauration d’un régime d’ART toute la vie (visites, formid = 16, 17) <p/><b>Denominateur: </b>Toute visite clinique pédiatrique jusqu’à visitdate pour la prescription de traitement multi-ART où “à des fins prophylactiques” n’est ne pas marquée, avec visitdate au cours de la période spécifiée', 'Clinical', 'Cliniques', -11),
(12, 1, 'Adult patient clinical visit with no ART eligibility or reason for eligibility recorded for patients prior to initiation of a life-long ART regimen', 'Visites cliniques de patients adultes sans informations d’éligibilité ARV ni raison d’éligibilité enregistrées pour les patients avant l’initiation d’un régime ARV permanent', '<b>Numerator: </b>Adult clinical visit held but no ART eligibility or reason for eligibility recorded for patients prior to initiation of a life-long ART regimen (visits, formid = 1, 2) <p/><b>Denominator: </b>Any adult clinical visit up until visitdate for prescription of multi-drug ART regimen where “for prophylaxis” is not marked, with visitdate during the specified time period (visits, formid = 1, 2\\)', '<b>Numerateur: </b>Adulte visite clinique tenue mais pas d’admissibilité ART ni raison d’éligibilité enregistrée pour les patients avant l’instauration d’un régime d’ART toute la vie (visites, formid = 1, 2). <p/><b>Denominateur: </b> Tout adulte clinique visiter jusqu’à visitdate pour la prescription de traitement multi-ART où “à des fins prophylactiques” n’est pas marqué, avec visitdate au cours de la période spécifiée', 'Clinical', 'Cliniques', -11),
(13, 1, 'All patient clinical visit with no ART eligibility or reason for eligibility recorded for patients prior to initiation of a life-long ART regimen', 'Toutes les visites cliniques sans informations d’éligibilité ARV ni raison d’éligibilité enregistrées pour les patients avant l’initiation d’un régime ARV permanent', '<b>Numerator: </b> Numerator for C.11 plus C.12 (visits, formid = 1, 2, 16, 17)<p/><b>Denominator: </b>Denominator for C.11 plus C.12 (visits, formid = 1, 2, 16, 17)', '<b>Numerateur: </b>Numérateur pour C.11 plus C.12 (visites, formid = 1, 2, 16, 17)<p/><b>Denominateur: </b>Dénominateur pour C.11 plus C.12 (visites, formid = 1, 2, 16, 17)', 'Clinical', 'Cliniques', -13),
(14, 1, 'Pediatric clinical (HIV) visit held but no data recorded in either “Statut TB” or “Evaluation TB” section.', 'Visites cliniques pédiatriques VIH tenues, mais aucune donnée enregistrée  soit dans la section “Statut TB” ou “Evaluation du TB”.', '<b>Numerator: </b>Clinical visit held but no data recorded in either “Statut TB” or “Evaluation TB” section (visits, formid = 16, 17) <p/><b>Denominator: </b>Any HIV pediatric clinical visitdate during the specified time period (visits, formid = 16, 17)', '<b>Numerateur: </b>Visite clinique qui s’est tenue, mais aucune donnée enregistrée dans section « Statut TB » ou « TB évaluation » (visites, formid = 16, 17) <p/><b>Denominateur: </b>Tout visitdate clinique pédiatrique du VIH au cours de la période de temps spécifiée (visites, formid = 16, 17)', 'Clinical', 'Cliniques', -14),
(15, 1, 'Adult clinical (HIV) visit held but no data recorded in either “Statut TB” or “Evaluation TB” section', 'Visites cliniques adultes VIH tenues, mais aucune donnée enregistrée  soit dans la section “Statut TB” ou “Evaluation du TB”.', '<b>Numerator: </b>Clinical visit held but no data recorded in either “Statut TB” or “Evaluation TB” section (visits, formid = 1, 2) <p/><b>Denominator: </b>Any adult clinical visitdate during the specified time period (visits, formid = 1, 2)', '<b>Numerateur: </b>Visite clinique qui s’est tenue, mais aucune donnée enregistrée dans section « Statut TB » ou « TB évaluation » (visites, formid = 1, 2). <p/><b>Denominateur: </b>N’importe quel adulte visitdate clinique au cours de la période de temps spécifiée (visites, formid = 1, 2)', 'Clinical', 'Cliniques', -15),
(16, 1, 'Clinical visit (HIV) held but no data recorded in either “Statut TB” or “Evaluation TB” section.', 'Toutes les visites cliniques VIH tenues, mais aucune donnée enregistrée dans section « Statut TB » ou « TB évaluation ».', '<b>Numerator: </b>Numerator for C.14 plus C.15 (visits, formid = 1, 2, 16, 17) <p/><b>Denominator: </b>Denominator for C.14 plus C.15 (visits, formid = 1, 2, 16, 17)', '<b>Numerateur: </b>Numérateur pour C.14 plus C.15 (visites, formid = 1, 2, 16, 17) <p/><b>Denominateur: </b>Dénominateur pour C.14 plus C.15 (visites, formid = 1, 2, 16, 17)', 'Clinical', 'Cliniques', -32),
(17, 1, 'No HIV discontinuation form completed for pediatric patient following last clinical visit, where last clinical visit recorded >180 days ago', 'Aucune forme d’abandon VIH remplie pour les patients pédiatriques après leur dernière visite clinique, où la dernière visite clinique enregistré > il y a 180 jours', '<b>Numerator: </b>No discontinuation form visitdate after last clinical visit, where visitdate for  last clinical visit (formid = 1, 2, 3, 4, 5, 6, 13, 14, 16, 17, 18, 19, or 20) recorded >180 days before end date of specified time period.  To be considered complete, form must have visit date PLUS “Oui” indication of program discontinuation OR reason for discontinuation (patients, formid =  21). <p/><b>Denominator: </b>Any HIV patient where visitdate for  last clinical visit (formid = 1, 2, 3, 4, 5, 6, 13, 14, 16, 17, 18, 19, or 20) was recorded >180 days before end date of specified time period  (patients)', '<b>Numerateur: </b>Aucun abandon forme visitdate après la dernière visite clinique, où visitdate pour dernière visite clinique (formid = 1, 2, 3, 4, 5, 6, 13, 14, 16, 17, 18, 19 ou 20) enregistré > 180 jours avant la date de la fin de la période de temps spécifiée.  Pour être considéré comme complet, le formulaire doit avoir visite jour PLUS « Oui » indication d’interruption de programme raison d’OR de l’abandon (patients, formid = 21). <p/><b>Denominateur: </b>Tout patient VIH où visitdate pour dernière visite clinique (formid = 1, 2, 3, 4, 5, 6, 13, 14, 16, 17, 18, 19 ou 20) a été enregistré > 180 jours avant la date de la fin de la période de temps spécifiée (patients)', 'Timeliness', 'Actualités', -17),
(18, 1, 'No HIV discontinuation form completed for adult patient following last clinical visit, where last clinical visit recorded >180 days ago', 'Aucune forme d’abandon VIH remplie pour les patients adultes après leur dernière visite clinique, où la dernière visite clinique enregistré > il y a 180 jours', '<b>Numerator: </b>No discontinuation form visitdate after last clinical visit, where visitdate for  last clinical visit (formid = 1, 2, 3, 4, 5, 6, 13, 14, 16, 17, 18, 19, or 20) recorded >180 days before end date of specified time period. To be considered complete, form must have visit date PLUS “Oui” indication of program discontinuation OR reason for discontinuation (patients, formid = 12). <p/><b>Denominator: </b>Any HIV patient where visitdate for  last clinical visit (formid = 1, 2, 3, 4, 5, 6, 13, 14, 16, 17, 18, 19, or 20) was recorded >180 days before end date of specified time period  ', '<b>Numerateur: </b>Aucun abandon forme visitdate après la dernière visite clinique, où visitdate pour dernière visite clinique (formid = 1, 2, 3, 4, 5, 6, 13, 14, 16, 17, 18, 19 ou 20) enregistré > 180 jours avant la date de la fin de la période de temps spécifiée. Pour être considéré comme complet, le formulaire doit avoir visite jour PLUS « Oui » indication d’interruption de programme raison d’OR de l’abandon (patients, formid = 12). <p/><b>Denominateur: </b>Tout patient VIH où visitdate pour dernière visite clinique (formid = 1, 2, 3, 4, 5, 6, 13, 14, 16, 17, 18, 19 ou 20) a été enregistré > 180 jours avant la date de la fin de la période de temps spécifiée (patients)', 'Timeliness', 'Actualités', -18),
(19, 1, 'No HIV discontinuation form completed for any patient following last clinical visit, where last clinical visit recorded >180 days ago', 'Aucune forme d’abandon VIH remplie pour des patients après leur dernière visite clinique, où la dernière visite clinique enregistré > il y a 180 jours', '<b>Numerator: </b>Numerator for C.17 plus C.18 (patients, formid = 12, 21) <p/><b>Denominator: </b>Denominator for C.17 plus C.18 (patients, formid = 12, 21)', '<b>Numerateur: </b>Numérateur pour C.17 plus C.18 (patients, formid = 12, 21) <p/><b>Denomianteur: </b>Dénominateur pour C.17 plus C.18 (patients, formid = 12, 21)', 'Timeliness', 'Actualités', -19),
(20, 1, 'Invalid components of DOB field', 'Composantes non valides du champ de la date de naissance', '<b>Numerator: </b>Invalid DD, MM or YY value for DOB (DD>31; MM>12; YY<1904 or YY>current YY); OR DOB > current DD/MM/YY (patients, formid = 10, 15)<p/><b>Denominator: </b>All registered patients (patients, formid = 10, 15)', '<b>Numerateur: </b>Valeur non valide de JJ, MM ou YY pour DOB (DD > 31 ; MM > 12 ; YY < 1904 ou YY > YY actuel) ; OU DOB > actuel JJ/MM/YY (patients, formid = 10 à 15) <p/><b>Denominateur: </b>Tous les patients (patients, formid = 10 à 15)', 'Demographic', 'Démographique', -20),
(21, 1, 'Discrepancy between DOB and age in years data fields', 'Écart entre la date de naissance et l’âge en années saisi', '<b>Numerator: </b>Difference in >1068 days (3 years) in comparison of “age in years” and “date of birth” fields (patients, formid = 10, 15) <p/><b>Denominator: </b>All registered patients (patients, formid = 10, 15)', '<b>Numerateur: </b>Difference de > 1068 jours (3 ans) en comparaison de « l’âge en années » et « date de naissance » champs (patients, formid = 10 à 15) <p/><b>Denominateur: </b>Tous les patients (patients, formid = 10 à 15)', 'Demographic', 'Démographique', -20),
(22, 1, 'Discrepancy between DOB values and components of national ID', 'Écart entre les valeurs de la date de naissance et les composantes de l’identité nationale', '<b>Numerator: </b>Difference in DOB values from MM or YY components of national ID (patients, formid = 10, 15) <p/><b>Denominator: </b>All registered patients (patients, formid = 10, 15)', '<b>Numerateur: </b>Différence de DOB les valeurs de composants MM ou YY d’identité nationale (patients, formid = 10 à 15) <p/><b>Denominateur: </b>Tous les patients (patients, formid = 10 à 15)', 'Demographic', 'Démographique', -20),
(23, 1, 'Patient registration type (adult vs. pediatric) conflicts with recorded age.', 'Conflit entre le type de fiche d''enregistrement (adulte vs enfant) et l’âge enregistré.', '<b>Numerator: </b> Patient registration type (adult vs. pediatric) conflicts with recorded age.  Patients of age<15 years recorded with adult registration form would be flagged, as would patients of age>15 years recorded with pediatric registration form. (patients, formid = 10, 15)<p/><b>Denominator: </b>All registered patients (patients, formid = 10, 15)', '<b>Numerateur:</b>Conflits de type (adulte et pédiatrique) d’inscription du patient avec l’âge enregistré.  Les patients d’âge < enregistrées avec le formulaire d’inscription adultes de 15 ans serait être marqués, comme le ferait des patients d’âge > 15 ans enregistré avec le formulaire d’inscription pédiatrique. (patients, formid = 10 à 15) <p/><b>Denominateur: </b>Tous les patients (patients, formid = 10 à 15)', 'Demographic', 'Démographique', -20),
(24, 1, 'Pediatric patient with marital status of married, widowed, separated/divorced, or living together', 'Patients pédiatriques avec l’état matrimonial de marié, veuf, séparé / divorcé, ou vivre ensemble', '<b>Numerator: </b>Pediatric patient with marital status of married, widowed, separated/divorced, or living together (patients, formid = 10, 15) <p/><b>Denominator: </b>All registered patients (patients, formid = 10, 15)', '<b>Numerateur: </b>Patient pédiatrique avec l’état matrimonial des mariées, veuves, séparées ou divorcées ou vivantes ensemble (patients, formid = 10 à 15) <p/><b>Denominateur: </b>Tous les patients (patients, formid = 10 à 15)', 'Demographic', 'Démographique', -20),
(25, 1, 'Any age accuracy flag present', 'Indicateur de non validité d''age présent ', '<b>Numerator: </b>Numerator any flag present for A.1 - A.5 (patients, formid = 10, 15) <p/><b>Denominator: </b>Any patient registered (patients, formid = 10, 15)', '<b>Numerateur: </b>Numérateur tout drapeau présente pour A.1 - A.5 (patients, formid = 10 à 15) <p/><b>Denominateur: </b>Tout patient inscrit (patients, formid = 10 à 15)', 'Demographic', 'Démographique', -20),
(26, 1, 'Obstetric history, pregnancy, or last menstrual period is recorded for a male patient; OR OB-GYN forms completed for male patient.', 'Antécédents obstétricaux, grossesse ou dernière période menstruelle est enregistré pour un patient de sexe masculin ; OU formulaire gynécologie remplis pour un patient de sexe masculin.', '<b>Numerator: </b>Clinical visit forms where obstetric history, pregnancy, or last menstrual period is recorded for a male patient (visits, formid = 1, 2, 27, 28); OR any data recorded on OB-GYN forms for male patient (formid = 24, 25, 26). <p/><b>Denominator: </b>Any clinical visit for male patient (visits, formid = 1, 2, 27, 28)', '<b>Numerateur: </b>Clinique visiter des formes où les antécédents obstétricaux, grossesse ou dernière période menstruelle est enregistrée pour un patient de sexe masculin (visites, formid = 1, 2, 27, 28) ; Toutes les données enregistrées sur les formes de gynécologie pour patient masculin (formid = 24, 25, 26). <p/><b>Denominateur: </b>Any clinical visit for male patient (visits, formid = 1, 2, 27, 28)', 'Demographic', 'Démographique', -21),
